mapper.select:
SELECT QT.tag_no, HT.tag_name FROM question_tag QT
JOIN qa_hashtag HT ON QT.tag_no = HT.tag_no
WHERE QT.qst_no = #{qst_no }

SELECT * FROM (SELECT ROWNUM rnum, Q.* FROM (SELECT Q.qst_no, Q.title, Q.qst_content, Q.qst_cnt, Q.qst_time, U.user_id, h.tag_name FROM qa_question Q INNER JOIN jejuuser U ON U.user_no = Q.user_no LEFT JOIN question_tag QT ON QT.qst_no = Q.qst_no LEFT JOIN qa_hashtag H ON H.tag_no = QT.tag_no
WHERE H.tag_name like '%'|| #{tagName} ||'%'
ORDER BY Q.qst_no DESC ) Q)NUM WHERE rnum BETWEEN #{paging.startNo} AND #{paging.endNo}
